pragma solidity ^0.8.0;
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./GamePassNFT.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";

contract GamePassMarketContract is Context, AccessControlEnumerable {
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    mapping(uint256 => bool) public _nonces;
    address payable public _treasureWallet;
    // mapping(address => bool) public _minted;

    address public _nftAddr;

    constructor(address nftAddr, address payable treasureWallet) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _nftAddr = nftAddr;
        _treasureWallet = treasureWallet;
    }

    event Minted(address user, uint256 tokenId, uint256 price, uint256 nonce);
    event Refilled(
        address user,
        uint256 tokenId,
        uint256 price,
        uint256 nonce,
        uint256 plays
    );
    modifier onlyManager() {
        require(
            hasRole(MANAGER_ROLE, _msgSender()),
            "SpinContract: Invalid sender"
        );
        _;
    }
    modifier onlyValidNonce(uint256 nonce) {
        require(!_nonces[nonce], "invalid nonce");
        _;
    }
    modifier onlyValidTime(uint256 deadline) {
        require(block.timestamp < deadline, "invalid timestamp");
        _;
    }
    modifier isMintable(address user) {
        // require(!_minted[user], "already minted");
        IERC721 nft = IERC721(_nftAddr);
        require(nft.balanceOf(user) == 0, "already has game pass");
        _;
    }

    function setTreasureWallet(
        address payable treasureWallet
    ) public onlyManager {
        _treasureWallet = treasureWallet;
    }

    function mint(
        uint256 tokenId,
        uint256 nonce,
        uint256 deadline,
        bytes calldata signature
    )
        public
        payable
        onlyValidNonce(nonce)
        onlyValidTime(deadline)
        isMintable(_msgSender())
    {
        bytes32 message = keccak256(
            abi.encodePacked(
                block.chainid,
                address(this),
                _msgSender(),
                tokenId,
                msg.value,
                nonce,
                deadline
            )
        );
        bytes32 msgHash = ECDSA.toEthSignedMessageHash(message);
        require(
            hasRole(MANAGER_ROLE, ECDSA.recover(msgHash, signature)),
            "invalid signature"
        );
        _nonces[nonce] = true;
        // _minted[_msgSender()] = true;
        GamePassNFT nft = GamePassNFT(_nftAddr);
        nft.mint(_msgSender(), tokenId);
        _treasureWallet.transfer(msg.value);
        emit Minted(_msgSender(), tokenId, msg.value, nonce);
    }

    function refill(
        uint256 tokenId,
        uint256 plays,
        uint256 nonce,
        uint256 deadline,
        bytes calldata signature
    ) public payable onlyValidNonce(nonce) onlyValidTime(deadline) {
        bytes32 message = keccak256(
            abi.encodePacked(
                block.chainid,
                address(this),
                _msgSender(),
                tokenId,
                plays,
                msg.value,
                nonce,
                deadline
            )
        );
        bytes32 msgHash = ECDSA.toEthSignedMessageHash(message);
        require(
            hasRole(MANAGER_ROLE, ECDSA.recover(msgHash, signature)),
            "invalid signature"
        );
        _nonces[nonce] = true;
        GamePassNFT nft = GamePassNFT(_nftAddr);
        require(
            nft.ownerOf(tokenId) == _msgSender(),
            "invalid owner of tokenId"
        );

        _treasureWallet.transfer(msg.value);
        emit Refilled(_msgSender(), tokenId, msg.value, nonce, plays);
    }

    receive() external payable {}
}
","keccak256":"0x3d8afc03a55841f9c29d0e75b3c5512a9c67bc846f8864e4053ac0f577b89af9","license":"UNLICENSED"},"contracts/GamePassNFT.sol":{"content":"//SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Burnable.sol";

contract GamePassNFT is ERC721Burnable, AccessControlEnumerable {
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");

    string public _baseURIPrefix;

    constructor(string memory name, string memory symbol) ERC721(name, symbol) {
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(MANAGER_ROLE, _msgSender());
    }

    modifier onlyManager() {
        require(hasRole(MANAGER_ROLE, _msgSender()), "Invalid sender");
        _;
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        virtual
        override(AccessControlEnumerable, ERC721)
        returns (bool)
    {
        return
            interfaceId == type(IERC721).interfaceId ||
            interfaceId == type(AccessControlEnumerable).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    /**
     * issue new nft
     * @param to receiver address
     * @param tokendId tokenId
     */
    function mint(address to, uint256 tokendId) public onlyManager {
        _safeMint(to, tokendId);
    }

    /**
     * update new baseURI
     * @param baseURIPrefix newBaseURI
     */
    function setBaseURI(string memory baseURIPrefix) public onlyManager {
        _baseURIPrefix = baseURIPrefix;
    }

    /**
     * override the baseURI which derived from ERC721
     */
    function _baseURI() internal view virtual override returns (string memory) {
        return _baseURIPrefix;
    }
}
","keccak256":"0x7d3e552d8347af642e92a2538de168eb07d51afb879040bf312df5a5fbea726e","license":"UNLICENSED"}},"version":1}
